mapper.select:
select

from exam


    class_id = #{classId}


    where exam_name like "%"#{searchStr}"%"


limit #{startIndex}, #{count}

mapper.insert:
insert into exam
(
  exam_name, grade_id, class_id, type, upload_status, course_str
)
values(
  #{examName}, #{gradeId}, #{classId}, #{type}, #{uploadStatus}, #{courseStr}
)
