mapper.select:
select

from inform_message
where org_id = #{orgId}
limit #{startIndex}, #{count}

mapper.insert:
insert into inform_message(
  org_id, grade_id, class_id, type, name, text, created, op_id, op_name, deleted
)
values(
    #{orgId}, #{gradeId}, #{classId}, #{type}, #{name}, #{text}, #{created}, #{opId}, #{opName}, #{deleted}
)
