mapper.select:
select

from op_log
where org_id = #{orgId} and sid = #{sid}
order by created desc
limit #{startIndex}, #{count}

mapper.insert:
insert into op_log
(
   org_id, sid, type, detail, op_id, op_time, created
)
values
(
  #{orgId}, #{sid}, #{type}, #{detail}, #{opId}, #{opTime}, #{created}
)
